
public class WhileLoopDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		
			int num,i,f=1;

			num = 12;

			i=1;

			while(i <= num)
			{
				f = f*i;
				i++;
	                         System.out.println("factoral="+f);
			}

		
		
     }

}
